import React, { useState } from "react";
import { Navbar } from "../../components";
import "./index.css";

export const CustosVariaveis = (props) => {
  const [custosVariaveis, setCustosVariaveis] = useState([]);
  const [descricao, setDescricao] = useState("");
  const [classificacao, setClassificacao] = useState("mes");
  const [valor, setValor] = useState("");

  // Função para adicionar um novo custo variável
  const adicionarCustoVariavel = () => {
    const novoCusto = {
      id: custosVariaveis.length + 1,
      descricao: descricao,
      classificacao: classificacao,
      valor: valor,
    };

    setCustosVariaveis([...custosVariaveis, novoCusto]);

    // Limpar os campos após adicionar o custo variável
    setDescricao("");
    setClassificacao("mes");
    setValor("");
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="content">
          <div className="bullets">
            <span className="bullet">1</span>
            <hr className="bullet-line" />
            <span className="bullet">2</span>
            <hr className="bullet-line" />
            <span className="bullet">3</span>
            <hr className="bullet-line" />
            <span className="bullet">4</span>
          </div>

          <div className="bullet-labels">
            <span id="lblB1" className="bullet-label">
              Custos Fixos
            </span>
            <span id="lblB2" className="bullet-label">
              Custos Variáveis
            </span>
            <span id="lblB3" className="bullet-label">?</span>
            <span id="lblB4" className="bullet-label">
              Enviar Solicitação
            </span>
          </div>

          <div className="container">
            <h3>Cadastrar Custos</h3>
            <div className="input-container">
              <div className="input-group">
                <label htmlFor="descricao">Descrição do Custo</label>
                <input
                  type="text"
                  id="descricao"
                  value={descricao}
                  onChange={(e) => setDescricao(e.target.value)}
                />
              </div>

              <div className="input-group">
                <label htmlFor="classificacao">Classificação</label>
                <select
                  id="classificacao"
                  value={classificacao}
                  onChange={(e) => setClassificacao(e.target.value)}
                >
                  <option value="mes">Mês</option>
                  <option value="ano">Ano</option>
                  <option value="litro">Litro (L)</option>
                  <option value="metro">Metro (M)</option>
                </select>
              </div>

              <div className="input-group">
                <label htmlFor="valor">Valor</label>
                <input
                  type="number"
                  id="valor"
                  value={valor}
                  onChange={(e) => setValor(e.target.value)}
                />
              </div>

              <div className="input-group">
                <button onClick={adicionarCustoVariavel}>Adicionar</button>
              </div>
            </div>

            {custosVariaveis.length > 0 && (
              <div className="custos-variaveis">
                <h3>Custos Variáveis Adicionados</h3>
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Descrição</th>
                      <th>Classificação</th>
                      <th>Valor</th>
                    </tr>
                  </thead>
                  <tbody>
                    {custosVariaveis.map((custo) => (
                      <tr key={custo.id}>
                        <td>{custo.id}</td>
                        <td>{custo.descricao}</td>
                        <td>{custo.classificacao}</td>
                        <td>{custo.valor}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CustosVariaveis;
